var fnAjax = {
	reqjson : function (url , callbacks) {
		var $url = url;
		$(this).submit(function () {
			var datas = $(this).serialize();
			var senddata = {
				type : "POST" , 
				data : datas , 
				dataType : "json" , 
				url : $url,
				headers: {'X-Requested-With': 'XMLHttpRequest'} , 
				success : function (data) {
					if ( typeof( callbacks ) == 'function' ) {
						callbacks( this , data );
					}	
				}
			};
			$.ajax(senddata);
			return false;
		});
	} 
};

jQuery.fn.extend(fnAjax);

var objAjax = {

	postjson : function (url , datas , callbacks) {
		var $url = url;
		var senddata = {
			type 	 : "POST" , 
			data 	 : datas , 
			dataType : "json" , 
			url 	 : $url,
			headers  : {'X-Requested-With': 'XMLHttpRequest'} , 
			success  : function (data) {
				if ( typeof( callbacks ) == 'function' ) {
					callbacks( data );
				}	
			}
		};

		return $.ajax(senddata);
	} 
};

jQuery.extend(objAjax);
